import React from 'react'
import { useState } from 'react'
import './ChatWindow.css'

// let messages = [
//     {
//         text: 'Hello'
//     },
//     {
//         text: 'test'
//     }
// ]


function ChatWindow() {
    const [messages, setMessage] = useState([])
    const [value, setValue] = useState('')
    const message = document.getElementById('message')

    function handleKeyDown(event){
        if (event.key === 'Enter') {
            console.log(value)
            message.value = ''
            setMessage([{value}])
            // console.log(messages)
        }
    };

    return (
        <div className='container-fluid chatContainer'>
            <div className='chatWindow'>
                {messages.map((item) => {
                    return (
                        <div className='chatBubble'>{item.text}</div>
                    )
                })}

            </div>
            <input id='message' placeholder='iMessage' className='messageInput' onKeyDown={(event) => handleKeyDown(event)} onChange={(e) => setValue(e.target.value)} />
        </div>
    )
}

export default ChatWindow
